import { Component, EventEmitter, Input, OnChanges, OnInit, Output } from '@angular/core';
import { Game } from '../models/game';
import { Player } from '../models/player';
import { StringService } from '../service/string.service';

@Component({
  selector: 'app-start',
  templateUrl: './start.component.html',
  styleUrls: ['./start.component.scss']
})
export class StartComponent implements OnInit, OnChanges {

  @Input() isNew!: boolean;
  @Input() message!: string;
  @Input() game!: Game;

  @Output() newGame: EventEmitter<Game> = new EventEmitter();

  public playerName: string = '';

  constructor(public stringService: StringService) {}

  public ngOnInit(): void {}

  public ngOnChanges(): void {
    if (this.message !== '') {
      this.isNew = true;
      this.playerName = this.message;
      if (this.message !== 'draw') {
        this.message = "~ " +
                        (this.message === "circle" ? "O's" : "X's") +
                       " Wins! ~";
      } else {
        this.message = "It's Draw!";
      }
    }
  }

  public startGame(choice: string): void {
    const p: Player = new Player().load({name: this.playerName, choice: choice});
    this.game.board = ['','','','','','','','',''];
    this.game.player = new Player().load(p);
    this.message = '';
    this.isNew = false;
    this.game.currentPlay = 'x';
    this.newGame.emit(this.game);
  }

  public resetGame(): void {
    this.isNew = true;
    this.message = '';
    this.playerName = '';

    this.game = new Game();
    this.game.board = ['','','','','','','','',''];
    this.game.player = new Player().load(this.game.player);
  }

}
<div id="message"
     [class]="'message ' + (message !== '' || isNew === true ? 'show' : '')"
     [style.visibility]="!isNew ? 'hidden' : 'visible'">

  <ng-container *ngIf="message === ''; else elseReset">

    <div class="start_game">
      <label>Choose an Option</label>
      <div class="user">
        <input #player_name="ngModel"
               id="player_name"
               name="player_name"
               type="text"
               placeholder="Player Name"
               [(ngModel)]="playerName">
      </div>
      <div class="player_choice">
        <div [class]="playerName !== '' ? 'team_choice choise_over' : 'team_choice disable'"
             (click)="playerName ? startGame('x') : ''">
          <div class="x">X</div>
        </div>
        <div [class]="playerName !== '' ? 'team_choice choise_over' : 'team_choice disable'"
             (click)="playerName ? startGame('circle') : ''">
          <div class="circle">O</div>
        </div>
      </div>
    </div>

  </ng-container>

  <ng-template #elseReset>

    <div *ngIf="playerName !== 'draw'">
      {{stringService.capitalize(game.player.name)}} has lost.
    </div>

    <p>{{message}}</p>

    <button id="reset"
            (click)="resetGame()">
      Reset Game
    </button>

  </ng-template>
</div>
